ction(event){					event.preventDefault();										/** close all other open divs **/							var currSection = $(this).parent('.client-banner').parent(".client").attr("id");												$(".client-banner").each(function(index){									if($(this).parent(".client").attr('id') !== currSection){								if($(this).children(".explore").is(":visible")){										console.log(currSection + " will be opened and " + $(this).parent(".client").attr('id') + " section will be closed" );																				//close any videos that were playing in the previous div 											$(this).children('.explore').children('.visual').children(".client-gallery").children(".vFrames").each(function(){												var f= $(this);												url = f.attr('src').split('?')[0];																							//vimeo videos to pause once the employee or consumer section is collapsed												function post(action, value) {													var data = { method: action };												  													if (value) {														data.value = value;													}																										f[0].contentWindow.postMessage(JSON.stringify(data), url);												}												 post('pause');										   });										   										$(this).children('.explore').fadeOut(1000);										$(this).children('.open').fadeIn(1000, function(){												$(this).siblings('.banner-img').fadeIn(500);													});										$(this).siblings(".close").hide();								}															}						});																$(this).fadeOut();						$(this).siblings('.banner-img').fadeOut(500);						$(this).parent(".client-banner").siblings(".close").fadeIn();						$(this).next('.explore').fadeIn(1000,function(){							return $(this).css('display','inline-block'); //to overwrite the generic display:block that jquery gives with fadeIn or show						});													var openPos = $(this).parent(".client-banner").parent(".client").offset().top - $("#work-section").offset().top;								console.log(openPos + " " + $("#work-section").offset().top + " new ones " + $(this).parent(".client-banner").parent(".client").attr("id"));																$('html, body').stop().animate({ scrollTop: openPos }, 1000, function() {									location.hash = $(openPos);  //attach the hash (#jumptarget) to the pageurl									$(this).parent(".client-banner").parent(".client").focus();								});						});						$('.close').click(function(event){				event.preventDefault();												$(this).siblings('.client-banner').children('.explore').children('.visual').children(".client-gallery").children(".vFrames").each(function(){			    	var f= $(this);			    	url = f.attr('src').split('?')[0];									//vimeo videos to pause once the employee or consumer section is collapsed			    	function post(action, value) {					    var data = { method: action };					  					    if (value) {					        data.value = value;					    }					    					    f[0].contentWindow.postMessage(JSON.stringify(data), url);					}			    	 post('pause');			       });				   				   $(this).siblings('.client-banner').children(".explore").fadeOut(1000);				   $(this).siblings(".client-banner").children('.open').fadeIn(1000, function(){					  	 $(this).siblings('.banner-img').fadeIn(500);					});				   $(this).hide();							});			//toggle leader section			$('.leader-section').click(function(event){					event.preventDefault();										$(this).children('.container').children('.more-info').slideToggle(1500);					/*$(this).closest(".leader-visual").toggle( 							function(){								 $(this).animate({height:265});							},							function(){								 $(this).animate({height:200});							}					);*/					$(this).children('.container').children('.more-info').focus();					$('html, body').stop().animate({ scrollTop: $(this).children('.container').position().top  }, 2000);								});									$('.client-gallery').each(function(){				var galleryPager = $(this).parent('.visual').parent('.explore').parent('.expand-clie