.parseUsername = function() {			return this.replace(/[@]+[A-Za-z0-9-_]+/g, function(u) {				var username = u.replace("@","")				//return u.link("http://twitter.com/"+username);				return '<a href="http://twitter.com/'+username+'" target="_blank">'+u+'</a>'			});		};		String.prototype.parseHashtag = function() {			return this.replace(/[#]+[A-Za-z0-9-_]+/g, function(t) {				var tag = t.replace("#","%23")				//return t.link("https://twitter.com/search?q="+tag);				return '<a href="http://twitter.com/search?q='+tag+'" target="_blank">'+t+'</a>'			});		};				$('#collega-tweets li:nth-child(odd)').addClass('alternate');		$('.collega-tweet-text').each(function(index){			    var tweet_text = $(this).html().parseURL().parseUsername().parseHashtag();					$(this).html(tweet_text);		});		//vimeo video players        $(".vThumbs > li").mouseover(function(){             var players;			    $('.vFrames').each(function(){			    	var f= $(this);			    	url = f.attr('src').split('?')[0];			    	function post(action, value) {					    var data = { method: action };					    					    if (value) {					        data.value = value;					    }					    					    f[0].contentWindow.postMessage(JSON.stringify(data), url);					}			    	post('pause');			    });        });			function onMessageReceived(e) {			    var data = JSON.parse(e.data);			 			    // Add listeners here			    if (data.event === 'ready') {			        post('addEventListener', 'play');			        post('addEventListener', 'pause');			        post('addEventListener', 'finish');			    }			}						if (window.addEventListener){			    window.addEventListener('message', onMessageReceived, false);			} else { // IE			    window.attachEvent('onmessage', onMessageReceived, false);			}				//Process		function clearHovers(otherprocess){			//$("#all-process li").each(function(){				$(otherprocess).each(function(index, val){				console.log(index + " " + val );  			//	var currProcess = $(this).