In();				});			}, function(e){							} 		 );		$('#create-logo').hover(			function(e){				clearHovers(["discover","plan","implement"]);				$(this).attr("src","img/process/create-icon-slide-1.png");				$(".create-label").stop().css({opacity:'0'});				//$(this).next('img').attr('src', )				$(this).stop().animate({width:'236px', height:'237px', display:'inline-block', top:'-3px', left:'0px',position:'relative', zIndex:'22'}, 1000, function(){					$(this).siblings("#create-slideshow").stop(true,true).fadeIn(1000).cycle({timeout:500});					$(this).siblings(".process-detail-text ").stop().css({display:'inline-block'}).stop(true,true).fadeIn();					$(this).siblings("#create-marker").stop().fadeIn();				}); 			}, function(e){							} 		 );		$('#implement-logo').hover(			function(e){				clearHovers(["discover","plan","create"]);			   $(this).attr("src","img/process/implement-icon-slide-1.png");				$(".implement-label").stop().css({opacity:'0'});				//$(this).next('img').attr('src', )				$(this).stop().animate({width:'236px', height:'237px', display:'inline-block', top:'-2px', left:'-31px',position:'relative', zIndex:'22'}, 1000, function(){					$(this).siblings("#implement-slideshow").stop(true,true).fadeIn(1000).cycle({timeout:500});					$(this).siblings(".process-detail-text ").stop().css({display:'inline-block'}).stop(true,true).fadeIn();					$(this).siblings("#implement-marker").stop().fadeIn();				});			}, function(e){							} 		 );    });	  </script>